short invSqrt(short x)
{
  short x1,x2,y1,y2;

  x1=x>>5;
  x2=x1+1;
  y1=lookup_table[x1];
  y2=lookup_table[x2];
  //printf("x1=%d,x2=%d,y1=%d,y2=%d\n",x1,x2,y1,y2);
  //printf("x-x1<<5=%d,y2-y1=%d\n",(x-(x1<<5)),(y2-y1));
  return(y1+(((x-(x1<<5))*(y2-y1))>>5));
}

